var gpio = require('rpi-gpio');
var pins = require('./pins');

function reset() {
    gpio.setup(pins.laser, gpio.DIR_OUT, null, function() {
        off();
    });
}

function set(onOff, cb) {
    gpio.write(pins.laser, onOff, cb);
};

function on(cb) {
    set(true, cb);
}

function off(cb) {
    set(false, cb);
}

module.exports = {
  reset: reset,
  set: set,
  on: on,
  off: off  
};